mapper.select:
select * from (select ROWNUM rnum, aa.* from
(select * from GOODS order by ${filterBy} desc) aa)
where rnum>${(cp-1)*ps} and rnum<=${cp*ps}

select * from (select ROWNUM rnum, aa.* from
(select * from GOODS where GOODS_CATEGORY_CODE=${gCode} order by ${filterBy} desc) aa)
where rnum>${(cp-1)*ps} and rnum<=${cp*ps}
