mapper.select:
select COUNT(LIKE_INDEX) from LIKE_TABLE 
where LIKE_TYPE_CODE=#{like_type_code} 
and LIKE_CONTENT_INDEX=#{like_content_index}
and LIKE_MEMBER_EMAIL=#{like_member_email}

mapper.delete:
      delete from LIKE_TABLE
      where LIKE_TYPE_CODE=#{like_type_code} 
	    and LIKE_CONTENT_INDEX=#{like_content_index}
	    and LIKE_MEMBER_EMAIL=#{like_member_email}
